mapper.select:
select campsite_no as no, campsite_name as name, campsite_sort as sort, campsite_latitude as latitude, campsite_longitude as longitude, campsite_address as address, campsite_tel as tel, campsite_sites as sites, campsite_price as price, campsite_likes as likes, campsite_hates as hates, campsite_photo as photo, campsite_point as point, campsite_create_date as createDate, campsite_used_yn as usedYn, campsite_sido as sido from( select row_number() over(order by campsite_no desc) as row_no, campsite_no, campsite_name, campsite_sort, campsite_latitude, campsite_longitude, campsite_address, campsite_tel, campsite_sites, campsite_price, campsite_likes, campsite_hates, campsite_photo, campsite_point, campsite_create_date, campsite_used_yn, campsite_sido from camp_campsites
campsite_name like '%' || #{keyword} || '%'
) where row_no >= #{begin} and row_no <= #{end}
