mapper.select:
select count(*) from camp_campsites
campsite_sido = #{city}
campsite_gugun = #{city}
and campsite_name like '%' || #{keyword} || '%'

select no, name, sort, latitude, longitude, address, tel, sites, price, likes, hates, photo, point, createDate, usedYN, myCampsiteNo from ( select row_number() over(order by CAMPSITE_NO desc) as row_no ,CAMPSITE_NO no ,CAMPSITE_NAME name ,CAMPSITE_SORT sort ,CAMPSITE_LATITUDE latitude ,CAMPSITE_LONGITUDE longitude ,CAMPSITE_ADDRESS address ,CAMPSITE_TEL tel ,CAMPSITE_SITES sites ,CAMPSITE_PRICE price ,CAMPSITE_LIKES likes ,CAMPSITE_HATES hates ,CAMPSITE_PHOTO photo ,CAMPSITE_POINT point ,CAMPSITE_CREATE_DATE createDate ,CAMPSITE_USED_YN usedYN ,CAMPSITE_SIDO sido ,CAMPSITE_GUGUN gugun ,MY_CAMPSITE_NO myCampsiteNo from camp_campsites
campsite_sido = #{city}
campsite_gugun = #{city}
and campsite_name like '%' || #{keyword} || '%'
) where row_no >= #{begin} and row_no <= #{end}

SELECT distinct CAMPSITE_GUGUN
FROM camp_campsites
where campsite_sido = #{city}
