mapper.select:
SELECT no,name,sort,latitude,longitude,address,tel,sites,price,likes,hates,photo,point,createDate,usedYn,"myCampsite.no",cnt,sido FROM (select X.* , row_number() over (
order by no desc
order by price desc
order by cnt desc
order by likes desc
) row_no from(SELECT campsite_no no, campsite_name name, campsite_sort sort, campsite_latitude latitude, campsite_longitude longitude, campsite_address address, campsite_tel tel, campsite_sites sites, campsite_price price, campsite_likes likes, campsite_hates hates, campsite_photo photo, campsite_point point, campsite_create_date createDate, campsite_used_yn usedYn, campsite_sido sido, my_campsite_no "myCampsite.no", (select count(*) from CAMP_REVIEW_BOARDS B WHERE B.campsite_no = A.campsite_no) cnt FROM CAMP_CAMPSITES A
campsite_address like '%' || #{citys} || '%'
) X ) WHERE row_no >= #{st} AND row_no <= #{en}
