mapper.update:
update CAMP_GOODS
set 
	  GOODS_NAME = #{name}
	, GOODS_PRICE = #{price}
	, GOODS_PHOTO = #{photo}
	, GOODS_POINT = #{goodsPoint}
	, GOODS_STOCK = #{stock}
	, GOODS_SORT = #{goodsSort}
	, GOODS_SUMMARY = #{summary}
	, GOODS_CATEGORY = #{goodsCategory}
	, GOODS_LONG_SUMMARY = #{longSummary}
where GOODS_NO = #{goodsNo}

mapper.select:
select GOODS_NO goodsNo , GOODS_NAME name , GOODS_PRICE price , GOODS_PHOTO photo , GOODS_STOCK stock , GOODS_SORT goodsSort , GOODS_SUMMARY summary , GOODS_CATEGORY goodsCategory , GOODS_LONG_SUMMARY longSummary , GOODS_POINT goodsPoint , GOODS_LIKES goodsLike , GOODS_HATES hate , GOODS_CREATE_DATE createDate , GOODS_FORSALE forsale from ( select ROW_NUMBER() OVER (ORDER BY GOODS_NO DESC) AS row_no , GOODS_NO , GOODS_NAME , GOODS_PRICE , GOODS_PHOTO , GOODS_POINT , GOODS_LIKES , GOODS_HATES , GOODS_CREATE_DATE , GOODS_STOCK , GOODS_SORT , GOODS_SUMMARY , GOODS_CATEGORY , GOODS_LONG_SUMMARY , GOODS_FORSALE from CAMP_GOODS
where GOODS_NAME = like '%' || #{keyword} || '%'
) where (row_no >= #{begin} and row_no <= #{end}) and GOODS_STOCK not in(0) and GOODS_FORSALE = 'Y' order by GOODS_NO asc

select GOODS_NO goodsNo , GOODS_NAME name , GOODS_PRICE price , GOODS_PHOTO photo , GOODS_STOCK stock , GOODS_SORT goodsSort , GOODS_SUMMARY summary , GOODS_CATEGORY goodsCategory , GOODS_LONG_SUMMARY longSummary , GOODS_POINT goodsPoint , GOODS_LIKES goodsLike , GOODS_HATES hate , GOODS_CREATE_DATE createDate , GOODS_FORSALE forsale from ( select ROW_NUMBER() OVER (ORDER BY GOODS_NO DESC) AS row_no , GOODS_NO , GOODS_NAME , GOODS_PRICE , GOODS_PHOTO , GOODS_POINT , GOODS_LIKES , GOODS_HATES , GOODS_CREATE_DATE , GOODS_STOCK , GOODS_SORT , GOODS_SUMMARY , GOODS_CATEGORY , GOODS_LONG_SUMMARY , GOODS_FORSALE from CAMP_GOODS
where GOODS_NAME like '%' || #{keyword} || '%'
) where (row_no >= #{begin} and row_no <= #{end}) order by GOODS_NO asc

select GOODS_NO goodsNo , GOODS_NAME name , GOODS_PRICE price , GOODS_PHOTO photo , GOODS_STOCK stock , GOODS_SORT goodsSort , GOODS_SUMMARY summary , GOODS_CATEGORY goodsCategory , GOODS_LONG_SUMMARY longSummary , GOODS_LIKES goodsLike , GOODS_HATES hate , GOODS_CREATE_DATE createDate , GOODS_FORSALE forsale from CAMP_GOODS
where GOODS_CATEGORY = #{cat}
where GOODS_SORT = #{type}
